package advent.year2016.day2;

import static org.junit.Assert.assertEquals;

import org.junit.Test;

public class BathroomSecurityTest {
    @Test
    public void getCode() {
        assertEquals(1985, BathroomSecurity.getCode("ULL\n" +
                "RRDDD\n" +
                "LURDL\n" +
                "UUUUD"));
        assertEquals(36629, BathroomSecurity.getCode("\n" +
                "\n" +
                "\n" +
                "\n" +
                ""));
    }

    @Test
    public void getAdvancedCode() {
        assertEquals("5DB3", BathroomSecurity.getAdvancedCode("ULL\n" +
                "RRDDD\n" +
                "LURDL\n" +
                "UUUUD\n"));
        assertEquals("99C3D", BathroomSecurity.getAdvancedCode("\n" +
                "\n" +
                "\n" +
                "\n" +
                ""));
    }
}
